import React from 'react';
import Header from '../component/Header';
import { motion } from 'framer-motion'
import { useState } from 'react';
import './Inscription.css';
import { useEffect } from 'react';


function Inscription() {

    const [scrollPercentage, setScrollPercentage] = useState(0);

    const handleScroll = () => {
        const scrollTop = window.pageYOffset || document.documentElement.scrollTop;
        const scrollHeight = document.documentElement.scrollHeight - document.documentElement.clientHeight;
        const currentScrollPercentage = (scrollTop / scrollHeight) * 100;
        setScrollPercentage(currentScrollPercentage);
    };

    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    // Use scrollPercentage in your component as needed

    return (
        <div>
            <div className='container'>
            <p>Scroll Percentage: {scrollPercentage}</p>
            <motion.h1 initial={{x: -1000}} animate={{x: scrollPercentage < 10 ? - 1000 + scrollPercentage * 100 : 0}}
                        transition={{type: 'tween'}}>BIENVENUE</motion.h1>
            <motion.h1 animate={{rotate: 90,}}
            style={{transformOrigin: 'center'}}>AU RAID</motion.h1>
            <motion.h1 animate={{rotate: -90}}>VERTICAL TITLE</motion.h1>
            </div>
            <div className='spacer'>
                <h1>SPACER</h1>
            </div>
        </div>
        
    );
}

export default Inscription;
