import React from 'react';
import Header from '../component/Header';
import { motion } from 'framer-motion'
import { useState } from 'react';
import './Inscription.css';
import { useEffect } from 'react';


function Inscription() {

    const [scrollPercentage, setScrollPercentage] = useState(0);

    const handleScroll = () => {
        const scrollTop = window.pageYOffset || document.documentElement.scrollTop;
        const scrollHeight = document.documentElement.scrollHeight - document.documentElement.clientHeight;
        const currentScrollPercentage = (scrollTop / scrollHeight) * 100;
        setScrollPercentage(currentScrollPercentage);
    };

    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    // Use scrollPercentage in your component as needed

    return (
        <div className='container'>
            <Header />
            <div className='header-spacer'></div>
            <motion.h1 animate={{x: scrollPercentage < 25 ? scrollPercentage * 4 : 100}}>Bienvenue</motion.h1>
            <h1>Au RAID</h1>
            <h1>CentraleSupélec</h1>
            <div className='spacer'>
                <h2>Le RAID CentraleSupélec est une association étudiante de l'école CentraleSupélec, qui organise chaque année un raid multisports de 3 jours dans une région de France.</h2>
                <h2>Le RAID est ouvert à tous, que vous soyez sportif ou non, et que vous soyez étudiant ou non.</h2>
                <h2>Le RAID est une occasion unique de découvrir une région de France, de rencontrer des gens et de vous dépasser.</h2>
            </div>
        </div>
    );
}

export default Inscription;
