import react from "react";
import { useState, useEffect } from "react";
import "./Inter.css";
import { motion } from "framer-motion";

function Inter({activated, setActivated}){

    const background = document.getElementsByClassName("main");

    useEffect(() => {
        // Code to run when the 'activated' value changes
        if(activated){
            background[0].style.backgroundColor = "blueviolet";
        } else {
            background[0].style.backgroundColor = "black";
        }
    }, [activated]);

    return(
        <div className="main">
            <motion.div className="circle" onClick={() => setActivated(!activated)} animate={{x: activated ? 0 : 50}}></motion.div>
        </div>
    );
}

export default Inter;
